export default {
  props: ["showNotification"],
  name: "ForumPage",
  data() {
    return {
      user: {
        avatar: "",
        name: "",
        email: "",
        username: "guest"
      },
      socket: null,
      newPost: {
        author: "",
        title: "",
        content: "",
        image: "",
        status: "public" // Default status
      },
      image: null,
      imagePreview: null,
      imageFileName: "",
      // Add this line to track file name
      posts: [],
      message: "",
      allowedUsers: [],
      selectedAllowedUsers: [],
      groups: [],
      otherUsers: [],
      followedUsers: [],
      // Track followed users
      // Chatbox data
      isChatExpanded: false,
      newMessage: "",
      selectedChatUser: null,
      chatUsers: [],
      currentMessage: {
        type: "",
        message: "",
        receiver: "",
        username: ""
      },
      showCreateGroup: false,
      newGroup: {
        title: "",
        description: "",
        creator_id: null
      },
      groupMembership: {},
      postsPrvMap: {},
      showNotifications: false,
      notifications: [],
      commentImages: {},
      unreadNotificationCount: 0,
      showImageModal: false,
      selectedImage: null
    };
  },
  beforeRouteEnter(to, from, next) {
    fetch("http://localhost:8080/api/info", {
      method: "GET",
      credentials: "include"
    }).then(res => {
      if (res.ok) {
        next();
      } else {
        next('/login');
      }
    }).catch(() => {
      next('/login');
    });
  },
  async created() {
    this.$router.push('/home');
    try {
      const userRes = await fetch("http://localhost:8080/api/info", {
        method: "GET",
        credentials: "include"
      });
      const userData = await userRes.json();
      console.log("userData", userData);
      this.user.name = userData.Username;
      this.user.email = userData.Email;
      this.user.username = userData.Username.toLowerCase();
      if (userData.Avatar) {
        this.user.avatar = `http://localhost:8080/uploads/${userData.Avatar}`;
      } else {
        this.user.avatar = `https://api.dicebear.com/7.x/avataaars/svg?seed=${userData.Username}`;
      }
      // Initialize WebSocket
      this.initializeWebSocket();
      await this.fetchPostsPrv();
      await this.fetchPosts();
      await this.fetchAllUsers();
      await this.fetchAllowedUsers();
      await this.fetchGroups();
      await this.fetchNotifications();
    } catch (err) {
      console.error("Initialization failed:", err);
      this.$router.push("/login");
    }
  },
  methods: {
    async fetchAllowedUsers() {
      try {
        const res = await fetch("http://localhost:8080/api/postsprivacy", {
          method: "GET",
          credentials: "include"
        });
        if (res.ok) {
          const data = await res.json();
          if (!data.followers) {
            return;
          }
          console.log("Allowed users data:", data);
          this.allowedUsers = data.followers.map(user => ({
            username: user,
            avatar: `https://api.dicebear.com/7.x/avataaars/svg?seed=${user}`
          }));
        } else {
          this.showNotification("Failed to fetch allowed users", "error");
          console.error("Failed to fetch allowed users");
        }
      } catch (error) {
        this.showNotification("Failed to fetch allowed users", "error");
        console.error("Error fetching allowed users:", error);
      }
      console.log("Allowed users:", this.allowedUsers);
    },
    async fetchAllUsers() {
      try {
        // Fetch all users for follow list
        const allUsersRes = await fetch("http://localhost:8080/api/allusers", {
          method: "GET",
          credentials: "include"
        });
        if (allUsersRes.ok) {
          const allUsersData = await allUsersRes.json();
          if (!allUsersData || allUsersData.length === 0) {
            return;
          }
          console.log("All users data:", allUsersData);
          this.otherUsers = allUsersData.map(user => ({
            name: user.fullname,
            username: user.username,
            avatar: user.avatar ? `http://localhost:8080/uploads/${user.avatar}` : `https://api.dicebear.com/7.x/avataaars/svg?seed=${user.username}`,
            followed: user.followed || false // Track if the user is followed
          }));

          // Initialize followedUsers based on allowedUsers
          this.followedUsers = this.otherUsers.filter(user => user.followed);
          console.log("Followed users:", this.followedUsers);
        }

        // Fetch chat users from openchat endpoint
        const chatUsersRes = await fetch("http://localhost:8080/api/openchat", {
          method: "GET",
          credentials: "include"
        });
        if (chatUsersRes.ok) {
          const chatUsersData = await chatUsersRes.json();
          console.log("Chat users data:", chatUsersData);
          if (!chatUsersData || chatUsersData.length === 0) {
            return;
          } else {
            this.chatUsers = chatUsersData.map(user => ({
              id: user.id,
              fullname: user.full_name,
              name: user.username,
              messages: [],
              // Initialize empty messages array
              avatar: user.avatar ? `http://localhost:8080/uploads/${user.avatar}` : `https://api.dicebear.com/7.x/avataaars/svg?seed=${user.username}`
            }));
          }
        } else {
          this.showNotification("Failed to fetch chat users", "error");
          console.error("Failed to fetch chat users");
        }
      } catch (error) {
        console.error("Error fetching chat users:", error);
        this.showNotification("Error fetching chat users", "error");
      }
    },
    async handleChatClick() {
      if (!this.isChatExpanded) {
        await this.fetchChatUsers();
        this.selectedChatUser = null; // Reset selected user when opening chat
      }
      this.isChatExpanded = !this.isChatExpanded;
    },
    setupNotificationWebSocket() {
      // Register handler for real-time notifications
      notificationWebSocket.onNotification('forum-page', notification => {
        console.log('Received real-time notification:', notification);

        // Refresh notifications and count from server to ensure accuracy
        this.fetchNotifications();
      });
    },
    async fetchNotificationCount() {
      try {
        const res = await fetch('http://localhost:8080/api/notifications', {
          method: 'GET',
          credentials: 'include'
        });
        if (res.ok) {
          const data = await res.json();
          // Only update the count, don't store notifications unless popup is open
          this.unreadNotificationCount = (data || []).filter(n => !n.is_read).length;
        }
      } catch (err) {
        console.error('Failed to fetch notification count:', err);
      }
    },
    showFullImage(imageUrl) {
      this.selectedImage = imageUrl;
      this.showImageModal = true;
    },
    closeImageModal() {
      this.showImageModal = false;
      this.selectedImage = null;
    }
  },
  mounted() {
    // Set up notification WebSocket
    this.setupNotificationWebSocket();
    this.fetchPosts();
    this.fetchNotifications();
    this.fetchPostsPrv();
    document.addEventListener('click', this.handleNotifClose);
  },
  beforeUnmount() {
    // Clean up notification WebSocket
    notificationWebSocket.removeNotificationHandler('forum-page');
    document.removeEventListener('click', this.handleNotifClose);
  },
  computed: {}
};
