        console.error('Error fetching notifications:', e);
        this.notifications = [];
        this.unreadNotificationCount = 0;
      }
    },
    async markNotificationAsRead(notificationId) {
      console.log(notificationId);
      try {
        // Find the notification first
        const notification = this.notifications.find(n => n.id === notificationId);
        if (!notification) return;

        // Set a timeout to mark as read after 3 seconds
        setTimeout(async () => {
          try {
            const res = await fetch(`http://localhost:8080/api/markasread`, {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json'
              },
              credentials: 'include',
              body: JSON.stringify({
                notificationId,
                is_read: 1
              })
            });
            console.log("res", res);
            if (res.ok) {
              notification.is_read = true;
              // Update unread count
              this.unreadNotificationCount = this.notifications.filter(n => !n.is_read).length;
            }
          } catch (error) {
            console.error('Error marking notification as read:', error);
          }
        }, 3000); // 3 seconds delay
      } catch (error) {
        console.error('Error marking notification as read:', error);
      }
    },
    handleImageUpload(event, postId = null) {
      const file = event.target.files[0];
      if (file) {
        const allowedTypes = ["image/jpeg", "image/png", "image/gif"];
        if (!allowedTypes.includes(file.type)) {
          this.showNotification('Only JPEG, PNG, and GIF images are allowed', 'error');
          event.target.value = ''; // Clear the input
          return;
        }
        // Validate file size (2MB limit)
        const maxSize = 2 * 1024 * 1024; // 2 MB in bytes
        if (file.size > maxSize) {
          this.showNotification('Image file size must be less than 2MB', 'error');
          event.target.value = ''; // Clear the input
          return;
        }
        if (postId) {
          // Handle comment image
          this.commentImages[postId] = {
            file: file,
            preview: URL.createObjectURL(file),
            fileName: file.name
          };
        } else {
          // Handle post image
          this.image = file;
          this.imageFileName = file.name;
          this.imagePreview = URL.createObjectURL(file);
        }
      } else {
        if (postId) {
          // Clear comment image
          this.commentImages[postId] = null;
        } else {
          // Clear post image
          this.image = null;
          this.imagePreview = null;
          this.imageFileName = "";
        }
      }
    },
    formatTime(timestamp) {
      if (!timestamp) return '';
      const date = new Date(timestamp);
      const now = new Date();
      const diff = now - date;

      // Less than 1 minute
      if (diff < 60000) {
        return 'just now';
      }
      // Less than 1 hour
      if (diff < 3600000) {
        const minutes = Math.floor(diff / 60000);
        return `${minutes}m ago`;
      }
      // Less than 24 hours
      if (diff < 86400000) {
        const hours = Math.floor(diff / 3600000);
        return `${hours}h ago`;
      }
      // Less than 7 days
      if (diff < 604800000) {
        const days = Math.floor(diff / 86400000);
        return `${days}d ago`;
      }
      // Otherwise show the date
      return date.toLocaleDateString('en-US', {
        month: 'short',
        day: 'numeric',
        year: 'numeric'
      });
    },
    async fetchChatUsers() {
      try {
        const chatUsersRes = await fetch("http://localhost:8080/api/openchat", {
          method: "GET",
          credentials: "include"
        });
        if (chatUsersRes.ok) {
          const chatUsersData = await chatUsersRes.json();
          console.log("Chat users data:", chatUsersData);
          if (!chatUsersData || chatUsersData.length === 0) {
            return;
          } else {
            this.chatUsers = chatUsersData.map(user => ({
              id: user.id,
              fullname: user.full_name,
              name: user.username,
              messages: [],
              avatar: user.avatar ? `http://localhost:8080/uploads/${user.avatar}` : `https://api.dicebear.com/7.x/avataaars/svg?seed=${user.username}`
            }));
          }
        } else {
          this.showNotification("Failed to fetch chat users", "error");
          console.error("Failed to fetch chat users");
        }
      } catch (error) {
        console.error("Error fetching chat users:", error);
        this.showNotification("Error fetching chat users", "error");
      }
    },
    async handleChatClick() {
      if (!this.isChatExpanded) {
        await this.fetchChatUsers();
        this.selectedChatUser = null; // Reset selected user when opening chat
      }
      this.isChatExpanded = !this.isChatExpanded;
    },
    setupNotificationWebSocket() {
      // Register handler for real-time notifications
      notificationWebSocket.onNotification('forum-page', notification => {
        console.log('Received real-time notification:', notification);

        // Refresh notifications and count from server to ensure accuracy
        this.fetchNotifications();
      });
    },
    async fetchNotificationCount() {
      try {
        const res = await fetch('http://localhost:8080/api/notifications', {
          method: 'GET',
          credentials: 'include'
        });
        if (res.ok) {
          const data = await res.json();
          // Only update the count, don't store notifications unless popup is open
          this.unreadNotificationCount = (data || []).filter(n => !n.is_read).length;
        }
      } catch (err) {
        console.error('Failed to fetch notification count:', err);
      }
    },
    showFullImage(imageUrl) {
      this.selectedImage = imageUrl;
      this.showImageModal = true;
    },
    closeImageModal() {
      this.showImageModal = false;
      this.selectedImage = null;
    }
  },
  mounted() {
    // Set up notification WebSocket
    this.setupNotificationWebSocket();
    this.fetchPosts();
    this.fetchNotifications();
    this.fetchPostsPrv();
    document.addEventListener('click', this.handleNotifClose);
  },
  beforeUnmount() {
    // Clean up notification WebSocket
    notificationWebSocket.removeNotificationHandler('forum-page');
    document.removeEventListener('click', this.handleNotifClose);
  },
  computed: {}
};
